mapper.select:
select *
from employees E
where employee_no = #{value}

select count(*)
from students


		student_div = #{status}


		and student_grade = #{grade}


		and major_no = #{major}


		and student_name = #{name}


		and student_no = #{studentNo}


		and student_phone_number = #{tel}

select * from( select A.student_no as no, A.major_no as "major.no", B.major_name as "major.name", A.professor_no as "professor.no", A.student_name as name, A.student_password as password, A.student_birth as birth, A.student_gender as gender, A.student_email as email, A.student_phone_number as phoneNumber, A.student_address as address, A.student_grade as grade, A.transfer_yn as transferYn, A.student_create_date as createDate, A.student_div as div, A.student_photo_name as photoName, row_number() over (order by student_no desc) as rn from students A, majors B where A.major_no = B.major_no
and A.student_div = #{status}
and A.student_grade = #{grade}
and A.major_no = #{major}
and A.student_name = #{name}
and A.student_no = #{studentNo}
and A.student_phone_number = #{tel}
) where rn >= #{beginIndex} and rn <= #{endIndex}
